mapper.insert:

INSERT INTO TIME_SEAT(
	SEAT_NO,
	TIME_NO,
	STATUS
} 
VALUES
(
	SEAT_NO = #{SEAT_NO},
	TIME_NO = #{TIME_NO}
)

mapper.update:
UPDATE TIME_SEAT
SET STATUS = 1
WHERE SEAT_NO = #{SEAT_NO} AND TIME_NO = #{TIME_NO}

UPDATE TIME_SEAT
SET STATUS = 0
WHERE SEAT_NO = #{SEAT_NO} AND TIME_NO = #{TIME_NO}
